require 'rails_helper'

describe Api::V1::UsersController do
  include_context 'user_setup'

  describe 'POST create' do
    describe 'CLIENT registration' do
      context 'when valid credentials' do
        before do
          post :create, format: :json, params: { access_token: @client_access_token, email: 'datu.puti@puti.com', password: 'mypassword123', client_id: app.uid, client_secret: app.secret }
        end

        it 'returns user details' do
          expect( response ).to have_http_status( 201 )

          expect( JSON( response.body )['token'] ).not_to be nil
          expect( JSON( response.body )['refresh_token'] ).not_to be nil
          expect( JSON( response.body )['expires_in'] ).not_to be nil
        end
      end

      context 'when invalid credentials' do
        context 'when user email already exist' do
          before do
            post :create, format: :json, params: { access_token: @client_access_token, email: 'datu.puti@puti.com', password: 'mypassword123', client_id: app.uid, client_secret: app.secret }
          end

          it 'returns error email has already taken' do
          end
        end
      end
    end
  end

  fdescribe 'POST save avatar' do
    describe 'Client upload avatar' do
      context 'when image base64 present' do
        before do
          post :upload_avatar, format: :json, params: { access_token: @client_access_token, image_base64: image_base64, client_id: app.uid, client_secret: app.secret }
        end

        it 'successfully uploaded avatar' do
          binding.pry
        end
      end
    end
  end

  private

  def image_base64
    "data:image/jpeg;base64,"
  end
end